import "vant/es/image-preview/style";
import _ImagePreview from "vant/es/image-preview";
import "vant/es/uploader/style";
import _Uploader from "vant/es/uploader";
import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.for-each.js";
import "core-js/modules/es.iterator.reduce.js";
import { _getMsg, _getUnreadMsg, _sendMsg, tupianshangchuan_post2 } from "@/API/im.api";
// import { _uploadImage } from "@/API/fund.api";

import { mapGetters, mapMutations } from "vuex";
// import { tupianshangchuan_post } from "@/API/user";
export default {
  name: "CustomerService",
  components: {
    [_Uploader.name]: _Uploader
  },
  data() {
    return {
      list: [],
      value: "",
      lastMsgId: "",
      itemname: process.env.VUE_APP_ITEM_NAME,
      interval: null,
      unread: 0,
      finished: false,
      // 没有历史消息
      androidAttrs: null,
      token_url: "",
      userAvatar: "",
      count: "1"
    };
  },
  computed: {
    ...mapGetters({
      activeLang: "language"
    })
  },
  created() {
    this.count = localStorage.getItem("avater");

    // console.log('this.userAvatar ->', this.count);
    this.userAvatar = require(`@/assets/image/avatar/${this.count || "2"}.png`);
    if (this.$route.query.token) {
      this.token_url = this.$route.query.token;
    } else {
      if (localStorage.getItem("token")) {
        this.token_url = localStorage.getItem("token");
      }
    }
    if (this.$route.query.lang) {
      if (this.$route.query.lang == "cn") {
        this.handleSetLang("zh-CN");
        // {title:'繁体中文',key: 'zh-CN'},
        // {title: 'English',key: 'en-US'}
      } else {
        this.handleSetLang("en-US");
      }
    }
    this.fetchList();
    const model = navigator.userAgent;
    // 判断是否是安卓手机，是则是true
    this.androidAttrs = model.indexOf("Android") > -1 || model.indexOf("Linux") > -1;
  },
  methods: {
    ...mapMutations(["setLanguage"]),
    handleSetLang(lang) {
      // 设置i18n.locale 组件库会按照上面的配置使用对应的文案文件
      this.$i18n.locale = lang;
      // 提交mutations
      // this.setLanguage(lang);
    },
    onPreview(url) {
      // 预览
      _ImagePreview([url]);
    },
    showTime(index) {
      // 时间显示
      if (index === 0) {
        return true;
      }
      if (index === this.list.length - 1) {
        return false;
      }
      if (this.list[index].createtime.split(" ")[0] === this.list[index + 1].createtime.split(" ")[1]) {
        return false;
      }
    },
    afterRead(file) {
      // 文件上传
      // // this.$toast('请输入消息内容')
      this.$toast.loading({
        duration: 0
      });
      // _uploadImage(file,this.token_url,(percent) => {
      //     console.log(percent)
      // }).then(data => {
      //   // this.$toast('成功')
      //     this.$toast.clear()
      //     // this.send('img', data)
      // }).catch((e) => {
      //   // this.$toast('失败')
      //   // this.$toast(e)
      //     this.$toast.clear()
      // })
      var t = this;
      let formData = new FormData(); //通过formdata上传
      formData.append("file", file.file);
      formData.append("moduleName", "123");
      tupianshangchuan_post2(formData).then(res => {
        t.$toast.clear();
        console.log(res);
        t.send("img", res);
      }).catch(function (err) {
        console.log(err);
        this.$toast.clear();
      });
    },
    fetchList(message_id = "") {
      // 获取消息列表
      _getMsg({
        token: this.token_url,
        message_id
      }).then(data => {
        // this.lastMsgId
        if (data == null) {
          data = [];
        }
        if (!this.lastMsgId) {
          this.lastMsgId = data.length && data[data.length - 1]["id"];
        }
        if (data.length) {
          if (message_id) {
            // 加载更多
            this.lastMsgId = data[data.length - 1]["id"];
            this.list = [...data.reverse(), ...this.list];
          } else {
            this.list = [...this.list, ...data.reverse()];
            let hash = {};
            this.list = this.list.reduce(function (preVal, curVal) {
              hash[curVal.id] ? " " : hash[curVal.id] = true && preVal.push(curVal);
              return preVal;
            }, []);
          }
          if (data.length < 10) {
            this.finished = true;
          }
        } else {
          this.list = [{
            id: "1",
            send_receive: "receive",
            content: this.$t("你好，欢迎来到我们的平台"),
            type: "text"
          }];
        }
        if (!message_id) {
          this.clearInterval();
          this.interval = setInterval(() => {
            this.fetchList();
          }, 1000);
        }
        this.list.forEach(item => {
          data.forEach(item1 => {
            if (item.id == item1.id && item1.delete_status != item.delete_status) {
              item.delete_status = item1.delete_status;
            }
          });
        });
      });
    },
    onMore() {
      // 加载更多
      this.fetchList(this.lastMsgId);
    },
    clearInterval() {
      if (this.interval) {
        clearInterval(this.interval);
        this.interval = null;
      }
    },
    fetchUnread() {
      // 获取未读
      _getUnreadMsg().then(data => {
        this.unread = data;
        // console.log(data)
      });
    },
    onClickLeft() {
      // 返回
      this.$router.go(-1);
    },
    send(type = "text", content = "") {
      // 发送消息, img 也当消息text
      // console.log("fasong");
      // console.log(content);
      if (!content) {
        this.$toast(this.$t("请输入消息内容"));
        return;
      }
      _sendMsg(type, content, this.token_url).then(data => {
        console.log(data);
        this.value = "";
        // document.getElementById('bottom').click()
        this.fetchList();
      });
    }
  },
  beforeDestroy() {
    this.clearInterval();
  }
};
