import "core-js/modules/es.array.push.js";
// import { Toast } from "vant";
import Service from "@/page/customerService/pcIndex.vue";
import { is_mobile } from "@/utils/utis";
import { apiGetCustomerService } from "@/API/home.js";
import { getStatus } from "@/API/commodity";
import { HOST_URL } from "@/config";
export default {
  name: "submitSuccess",
  components: {
    Service
  },
  props: {
    sellerSign: {
      default: false
    },
    token: {
      default: ""
    }
  },
  data() {
    return {
      itemname: process.env.VUE_APP_ITEM_NAME,
      status: 0,
      screenWidth: document.body.clientWidth,
      labelPosition: "top",
      isShow: false,
      onlinePath: "",
      HOST_URL
    };
  },
  computed: {},
  created() {
    // this.getStatusInfo()

    if (this.screenWidth >= 500) {
      this.labelPosition = "left";
    } else {
      this.labelPosition = "top";
    }
    this.getOnlinePath();
  },
  methods: {
    async getOnlinePath() {
      let res = await apiGetCustomerService({
        code: "customer_service_url"
      });
      this.onlinePath = res.customer_service_url;
    },
    //查询店铺申请进度
    getStatusInfo() {
      getStatus({}).then(res => {
        this.status = res.status;
      });
    },
    closeService(val) {
      this.isShow = val;
    },
    openUrl(index) {
      switch (index) {
        case 1:
          if (this.labelPosition == "left") {
            window.location.href = location.origin + "/ww";
            // window.location.href = location.origin+"/ww"
          }
          if (this.labelPosition == "top") {
            if (parent !== window) {
              window.open(parent.location.origin + "/www");
            } else {
              window.location.href = location.origin + "/www";
            }
          }
          break;
        case 2:
          if (this.itemname == "FamilyShop") {
            im_create_iframe_client.open();
            return;
          }
          if (this.onlinePath) {
            window.open(this.onlinePath, "_blank");
          } else if (is_mobile()) {
            this.$router.push({
              path: "/customerServiceIndex",
              query: {
                token: this.token
              }
            });
          } else {
            this.isShow = true;
          }
          break;
        default:
          break;
      }
    }
  }
};
